jboncc_process_ajax_starthis(){$ret = "";$exp = explode("-",$_POST["ccstar"]);$font = $exp[0];$code = $exp[1];$ret = "";global $wpdb;$table_name1 = $wpdb->prefix . "iconswpbyjboncc_vectoricons";$querystr = " SELECT id, fontid, bookmarks FROM ".$table_name1." WHERE fontid = '".$font."' ";$obj_icons = $wpdb->get_results($querystr);if(sizeof($obj_icons) > 0){ foreach ($obj_icons as $icon){	$id = $icon->id;	$fontid = $icon->fontid;	$bookmarks = $icon->bookmarks;}if($_POST["star"] == "add"){ $bookmarks .= $_POST["ccstar"]."|";$wpdb->query( $wpdb->prepare(" UPDATE $table_name1 SET bookmarks = %s WHERE id= %d AND fontid= %s " , $bookmarks , $id , $fontid ));$ret = "Added";}else { $bookmarks = str_replace($_POST["ccstar"]."|","",$bookmarks);$wpdb->query( $wpdb->prepare(" UPDATE $table_name1 SET bookmarks = %s WHERE id= %d AND fontid= %s " , $bookmarks , $id , $fontid ));$ret = "Deleted";}}/*//echo $ret;*/die();}/*[ifont="v1" code="v1-untitled-1" size="24px" onsize="24px" color="#000000" oncolor="#ffffff" padding="8px" onpadding="8px" nobg="1" bg1="#cccccc" bg2="#eeeeee" onbg1="#333333" onbg2="#555555" borderwidth = "1px" bordercolor = "#ff0000" borderstyle = "solid" borderradius = "10%"effect = "fade" url="" target = ""]*/function iconswpbyjboncc_shortcode( $atts ) {$ret = "";	extract( shortcode_atts( array(		'font' => '',		'code' => '',		'size' => '24px',		'onsize' => '24px',		'color' => '#000000',		'oncolor' => '#000000',		'padding' => '0px',		'onpadding' => '0px',		'align' => 'none',		'margin' => '0px 0px 0px 0px',		'bgtype' => 'none',		'bg1' => 'transparent',		'bg2' => 'transparent',		'onbg1' => 'transparent',		'onbg2' => 'transparent',		'borderwidth' => '0px',		'borderstyle' => 'solid',		'bordercolor' => 'transparent',		'borderradius' => '0px',		'effect' => 'none',		'link' => '',		'target' => ''		), $atts ));if($target == "self"){$target = "_self";} else if($target == "new"){$target = "_blank";}$url = $link;include("iconswpbyjboncc_fontpath.php");$path = $fontpath[$font];$rand = rand();/*//$ret .= 'hi'.$url;*/$bg = $onbg = "";$ret .= '<link rel="stylesheet" type="text/css" href="'.plugins_url( '/_fonts/'.$path.'/style-font.css', __FILE__ ).'">';$alignstyle = "";if($align != "none"){if($align == "left"){$alignstyle = "position:relative;left:0%;";}if($align == "right"){$alignstyle = "position:relative;right:0%;float:right;";}if($align == "centre"){$alignstyle = "position:relative;left:50%;";}}if($bgtype != "none"){/*******bg******/if($bgtype == "color"){$bg = "background:".$bg1.";";} else if($bgtype == "gradient"){ $bg = "background-color:".$bg1.";background-image:-webkit-gradient(linear,left top,left bottom,from(".$bg1."),to(".$bg2."));background-image: -webkit-linear-gradient(top, ".$bg1.", ".$bg2."); background-image:    -moz-linear-gradient(top, ".$bg1.", ".$bg2.");background-image:     -ms-linear-gradient(top, ".$bg1.", ".$bg2.");background-image:      -o-linear-gradient(top, ".$bg1.", ".$bg2.");"; }/*******on bg******/if($bgtype == "color"){$onbg = "background:".$onbg1.";";} else if($bgtype == "gradient"){$onbg = "background-color:".$onbg1.";background-image:-webkit-gradient(linear,left top,left bottom,from(".$onbg1."),to(".$onbg2."));background-image: -webkit-linear-gradient(top, ".$onbg1.", ".$onbg2."); background-image:    -moz-linear-gradient(top, ".$onbg1.", ".$onbg2.");background-image:     -ms-linear-gradient(top, ".$onbg1.", ".$onbg2.");background-image:      -o-linear-gradient(top, ".$onbg1.", ".$onbg2.");";}}$addclass = "";if($effect == "fade"){ $addclass = "fadethis";}if($effect == "spin"){ $addclass = "spinthis";}$ret .= '<style type="text/css">.'.$font.$code.'-i-'.$rand.'{float:left;line-height:1;font-size:'.$size.';color:'.$color.';padding:'.$padding.';border:'.$borderwidth.' '.$borderstyle.' '.$bordercolor.';border-radius:'.$borderradius.';-webkit-border-radius:'.$borderradius.';-moz-border-radius:'.$borderradius.';'.$bg.';'.$alignstyle.';margin:'.$margin.';}.'.$font.$code.'-i-'.$rand.':hover{font-size:'.$onsize.';color:'.$oncolor.';padding:'.$onpadding.';'.$onbg.'}</style>';if(trim($url) != ""){$ret .= '<a href="'.$url.'" target="'.$target.'">';}$ret .= '<span class=" '.$addclass.' '.$font.$code.'-i-'.$rand.' " aria-hidden="true" data-icon-'.$font.'="&#x'.$code.';"></span>';if(trim($url) != ""){ $ret .= '</a>'; }return $ret;}add_shortcode('i', 'iconswpbyjboncc_shortcode');/*//add a button to the content editor, next to the media button//this button will show a popup that contains inline content*/add_action('media_buttons_context', 'iconswpbyjboncc_add_my_custom_button');/*//add some content to the bottom of the page //This will be shown in the inline modal*/add_action('admin_footer', 'iconswpbyjboncc_add_inline_popup_content');/*//action to add a custom button to the content editor*/function iconswpbyjboncc_add_my_custom_button($context) {$context .= "<a class='button' id='iconswpbyjboncc_addbutton' title='Pick and Insert Vector Icons' href='#'><span class='wp-media-buttons-icon wp-add-icon-button'></span>Add icon</a>";    return $context;}function iconswpbyjboncc_add_inline_popup_content() {?><div class='iconswpjboncc_addpagebg'>&nbsp;</div><div id="popup_container" class="expandiconsbox"><div class='iconsbox_close'>X</div><div class="iconswpjboncc_addicon"><?php iconswpbyjboncc_module(); ?></div></div><?php}?>